object _8_Largest_Product_In_A_Series {
  def main( args: Array[String] ): Unit = {
    val HOW_MANY = 13
    val str = ""

    var max = BigInt(0)
    var maxIndex = -1
    for (i <- 0 to str.length-HOW_MANY) {
      println("i="+i+", "+str.substring(i, i+HOW_MANY))
      val d1 = BigInt( str.charAt(i).toString.toInt )
      val d2 = BigInt( str.charAt(i+1).toString.toInt )
      val d3 = BigInt( str.charAt(i+2).toString.toInt )
      val d4 = BigInt( str.charAt(i+3).toString.toInt )
      val d5 = BigInt( str.charAt(i+4).toString.toInt )
      val d6 = BigInt( str.charAt(i+5).toString.toInt )
      val d7 = BigInt( str.charAt(i+6).toString.toInt )
      val d8 = BigInt( str.charAt(i+7).toString.toInt )
      val d9 = BigInt( str.charAt(i+8).toString.toInt )
      val d10 = BigInt( str.charAt(i+9).toString.toInt )
      val d11 = BigInt( str.charAt(i+10).toString.toInt )
      val d12 = BigInt( str.charAt(i+11).toString.toInt )
      val d13 = BigInt( str.charAt(i+12).toString.toInt )

      val prod = d1*d2*d3*d4*d5*d6*d7*d8*d9*d10*d11*d12*d13
      println("prod = " + prod)
      if (prod > max) {
        max = prod
        maxIndex = i
      }
    }
    println( str.substring( maxIndex, maxIndex+13 ) +" :"+maxIndex+" -> "+ max)
  }
}
